/* Platform configuration header for ServerLink */
/* This file is generated by CMake from config.h.in */

#ifndef SERVERLINK_CONFIG_H
#define SERVERLINK_CONFIG_H

/* Platform I/O multiplexing support */
/* #undef SL_HAVE_WEPOLL */
#define SL_HAVE_EPOLL 1
/* #undef SL_HAVE_KQUEUE */
#define SL_HAVE_SELECT 1

/* Event notification support */
#define SL_HAVE_EVENTFD 1

/* TCP keepalive options */
#define SL_HAVE_TCP_KEEPIDLE 1
#define SL_HAVE_TCP_KEEPINTVL 1
#define SL_HAVE_TCP_KEEPCNT 1
/* #undef SL_HAVE_TCP_KEEPALIVE_VALS */

/* Socket options */
/* #undef SL_HAVE_SO_NOSIGPIPE */
#define SL_HAVE_MSG_NOSIGNAL 1

/* IPC (Unix Domain Sockets) support */
#define SL_HAVE_IPC 1

/* C++20 Feature Support */
#define SL_HAVE_CONCEPTS 1
#define SL_HAVE_RANGES 1
#define SL_HAVE_SPAN 1
#define SL_HAVE_STD_FORMAT 1
#define SL_HAVE_SOURCE_LOCATION 1
#define SL_HAVE_THREE_WAY_COMPARISON 1
#define SL_HAVE_LIKELY 1
#define SL_HAVE_CONSTEVAL 1

/* Performance monitoring */
/* #undef SL_ENABLE_MONITORING */

/* Determine the best I/O poller to use */
/* Priority: wepoll (Windows) > epoll (Linux) > kqueue (BSD/macOS) > select (fallback) */
#if SL_HAVE_WEPOLL
    #define SL_USE_WEPOLL 1
    #define SL_POLLER_NAME "wepoll"
#elif SL_HAVE_EPOLL
    #define SL_USE_EPOLL 1
    #define SL_POLLER_NAME "epoll"
#elif SL_HAVE_KQUEUE
    #define SL_USE_KQUEUE 1
    #define SL_POLLER_NAME "kqueue"
#elif SL_HAVE_SELECT
    #define SL_USE_SELECT 1
    #define SL_POLLER_NAME "select"
#else
    #error "No I/O multiplexing mechanism available"
#endif

/* Version information */
#define SL_VERSION_MAJOR 0
#define SL_VERSION_MINOR 1
#define SL_VERSION_PATCH 0
#define SL_VERSION_STRING "0.1.0"

#endif /* SERVERLINK_CONFIG_H */
